sys_ws_operation.operation_script:
(function process(request, response) {

    try {
        // Parse request body
        var requestBody = request.body.data;

        gs.info('[Jira Webhook] ========== NEW REQUEST ==========');
        gs.info('[Jira Webhook] Full payload: ' + JSON.stringify(requestBody));

        // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å Jira webhook
        var issueKey = requestBody.issue_key || requestBody.issue.key;
        var status = requestBody.status || requestBody.issue.fields.status.name;

        gs.info('[Jira Webhook] Issue Key: ' + issueKey);
        gs.info('[Jira Webhook] Status: ' + status);

        // Validate
        if (!issueKey || !status) {
            gs.warn('[Jira Webhook] Missing issue_key or status');
            response.setStatus(400);
            response.setBody({
                status: 'error',
                message: 'Missing issue_key or status'
            });
            return;
        }

        // ‡πÄ‡∏ä‡πá‡∏Ñ‡∏ß‡πà‡∏≤ status = Done ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
        if (status !== 'Done' && status !== 'Closed') {
            gs.info('[Jira Webhook] Status not Done/Closed: ' + status);
            response.setStatus(200);
            response.setBody({
                status: 'success',
                message: 'Status not Done, no action taken'
            });
            return;
        }

        // ‡∏´‡∏≤ Parking Reservation ‡∏à‡∏≤‡∏Å Jira Issue Key
        var parking = new GlideRecord('x_1869622_parkin_0_parking_reservation');
        parking.addQuery('jira_issue_key', issueKey);
        parking.addQuery('reservation_status', 'pending');
        parking.setLimit(1);
        parking.query();

        if (parking.next()) {

            var parkingNumber = parking.getValue('number');
            var requesterName = parking.requester_name.getDisplayValue();
            var requesterEmail = parking.getValue('requester_email');

            gs.info('[Jira Webhook] Found parking: ' + parkingNumber);
            gs.info('[Jira Webhook] Requester: ' + requesterName + ' (' + requesterEmail + ')');

            // ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞
            parking.setValue('reservation_status', 'confirmed');
            parking.setValue('jira_status', status);
            parking.work_notes = '‚úì Auto-confirmed from Jira via Webhook\n' +
                                'Status: ' + status + '\n' +
                                'Updated: ' + new GlideDateTime().getDisplayValue() + '\n' +
                                'Issue Key: ' + issueKey;

            parking.update();

            gs.info('[Jira Webhook] Status updated to confirmed');

            // ‡∏™‡πà‡∏á‡∏≠‡∏µ‡πÄ‡∏°‡∏•
            var emailSent = sendConfirmationEmail(parking);

            // Response
            response.setStatus(200);
            response.setBody({
                status: 'success',
                parking_number: parkingNumber,
                new_status: 'confirmed',
                email_sent: emailSent,
                message: 'Parking confirmed and email sent'
            });

        } else {
            gs.warn('[Jira Webhook] Parking not found for issue: ' + issueKey);
            gs.warn('[Jira Webhook] Or already confirmed');

            response.setStatus(404);
            response.setBody({
                status: 'error',
                message: 'Parking reservation not found or already confirmed',
                issue_key: issueKey
            });
        }

    } catch (ex) {
        gs.error('[Jira Webhook] Exception: ' + ex.message);
        gs.logError('[Jira Webhook] Stack trace: ' + ex.stack);

        response.setStatus(500);
        response.setBody({
            status: 'error',
            message: 'Server error: ' + ex.message
        });
    }


    // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡πà‡∏á‡∏≠‡∏µ‡πÄ‡∏°‡∏•
    function sendConfirmationEmail(parkingRecord) {
        try {
            var email = new GlideEmailOutbound();

            // From
            email.setFrom('noreply@company.com');

            // To
            var recipientEmail = parkingRecord.getValue('requester_email');
            if (!recipientEmail || recipientEmail == '') {
                gs.warn('[Jira Webhook] No recipient email');
                return false;
            }
            email.addAddress(recipientEmail);

            // Subject
            var subject = '‚úì Parking Confirmed - ' + parkingRecord.getValue('number');
            email.setSubject(subject);

            // Body (HTML)
            var body = buildEmailBody(parkingRecord);
            email.setBody(body);

            // Send
            email.send();

            gs.info('[Jira Webhook] Email sent to: ' + recipientEmail);
            return true;

        } catch (ex) {
            gs.error('[Jira Webhook] Email error: ' + ex.message);
            return false;
        }
    }


    // ‡∏™‡∏£‡πâ‡∏≤‡∏á Email Body
    function buildEmailBody(parking) {
        var body = '<html><body style="font-family: Arial, sans-serif;">';
        body += '<div style="max-width: 600px; margin: 0 auto; padding: 20px; border: 1px solid #ddd;">';

        // Header
        body += '<h2 style="color: #2c5282;">‚úÖ Parking Reservation Confirmed!</h2>';
        body += '<p>Dear <strong>' + parking.requester_name.getDisplayValue() + '</strong>,</p>';
        body += '<p>Your parking reservation has been <strong style="color: green;">confirmed</strong>!</p>';

        // Details Section
        body += '<div style="background: #f7fafc; padding: 15px; border-radius: 5px; margin: 20px 0;">';
        body += '<h3 style="margin-top: 0;">üìã Reservation Details</h3>';
        body += '<table style="width: 100%;">';
        body += '<tr><td style="padding: 5px;"><strong>Number:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.getValue('number') + '</td></tr>';
        body += '<tr><td style="padding: 5px;"><strong>Vehicle:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.getValue('vehicle_registration_number') + '</td></tr>';
        body += '<tr><td style="padding: 5px;"><strong>Car Type:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.getValue('car_type') + '</td></tr>';
        body += '<tr><td style="padding: 5px;"><strong>Location:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.parking_location.getDisplayValue() + '</td></tr>';
        body += '</table>';
        body += '</div>';

        // Date Section
        body += '<div style="background: #fff5f5; padding: 15px; border-radius: 5px; border-left: 4px solid #f56565; margin: 20px 0;">';
        body += '<h3 style="margin-top: 0; color: #c53030;">‚è∞ Important Dates</h3>';
        body += '<table style="width: 100%;">';
        body += '<tr><td style="padding: 5px;"><strong>Start:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.start_date_time.getDisplayValue() + '</td></tr>';
        body += '<tr><td style="padding: 5px;"><strong>End:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.end_date_time.getDisplayValue() + '</td></tr>';
        body += '<tr><td style="padding: 5px;"><strong>Duration:</strong></td>';
        body += '<td style="padding: 5px;">' + parking.getValue('duration_hours') + ' hours</td></tr>';
        body += '</table>';
        body += '</div>';

        // Payment Section
        body += '<div style="background: #f0fff4; padding: 15px; border-radius: 5px; border-left: 4px solid #48bb78; margin: 20px 0;">';
        body += '<h3 style="margin-top: 0; color: #22543d;">üí∞ Payment</h3>';
        body += '<p style="font-size: 24px; font-weight: bold; color: #22543d; margin: 10px 0;">';
        body += parking.getValue('parking_fee') + ' THB';
        body += '</p>';
        body += '</div>';

        // Footer
        body += '<div style="margin-top: 30px; padding-top: 20px; border-top: 1px solid #ddd;">';
        body += '<p style="color: #666; font-size: 14px;">';
        body += 'Please arrive on time and show this confirmation email at the parking entrance.';
        body += '</p>';
        body += '<p style="color: #666; font-size: 14px;">';
        body += '<strong>Jira Ticket:</strong> ' + parking.getValue('jira_issue_key') + '<br>';
        body += '<strong>Status:</strong> ‚úÖ Confirmed';
        body += '</p>';
        body += '</div>';

        // View Details Button
        var instanceUrl = gs.getProperty('glide.servlet.uri');
        var recordUrl = instanceUrl + 'x_1869622_parkin_0_parking_reservation.do?sys_id=' + parking.sys_id;
        body += '<div style="margin-top: 20px; text-align: center;">';
        body += '<a href="' + recordUrl + '" ';
        body += 'style="display: inline-block; background: #2c5282; color: white; ';
        body += 'padding: 12px 30px; text-decoration: none; border-radius: 5px; font-weight: bold;">';
        body += 'View Full Details';
        body += '</a>';
        body += '</div>';

        // Disclaimer
        body += '<div style="margin-top: 30px; padding-top: 20px; border-top: 1px solid #ddd; text-align: center; color: #999; font-size: 12px;">';
        body += '<p>This is an automated message from Parking Reservation System</p>';
        body += '<p>Please do not reply to this email</p>';
        body += '</div>';

        body += '</div>';
        body += '</body></html>';

        return body;
    }

})(request, response);
