catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    if (isLoading || isTemplate) {
        return;
    }

    if (!newValue) {
        return;
    }

    var startDate = new Date(newValue);
    var now = new Date();

    // ========================================
    // ตรวจสอบ: Start ไม่เป็นอดีต
    // ========================================

    if (startDate < now) {
        g_form.showFieldMsg(
            'start_date_time',
            '❌ ไม่สามารถจองเวลาย้อนหลังได้',
            'error'
        );

        g_form.clearValue('start_date_time');
        g_form.clearValue('end_date_time');
        g_form.clearValue('duration_hours');
        g_form.clearValue('parking_fee');

        return;
    }

    // ========================================
    // ตรวจสอบ End Date ถ้ามี
    // ========================================

    var endDateTime = g_form.getValue('end_date_time');

    if (endDateTime) {
        var endDate = new Date(endDateTime);

        // ถ้า End <= Start ให้ล้าง End
        if (endDate <= startDate) {
            g_form.showFieldMsg(
                'start_date_time',
                '⚠️ เวลาเริ่มต้นมากกว่าเวลาสิ้นสุด กรุณาเลือกเวลาสิ้นสุดใหม่',
                'warning',
                3000
            );

            g_form.clearValue('end_date_time');
            g_form.clearValue('duration_hours');
            g_form.clearValue('parking_fee');
        }
    }

    // ล้าง error
    g_form.hideFieldMsg('start_date_time', true);

    // แสดง success
    g_form.showFieldMsg(
        'start_date_time',
        '✓ วันที่ถูกต้อง',
        'info',
        2000
    );
}
