sys_script.script:
// Business Rule: Check Parking Space Availability
// Table: x_xxxxx_parking_reservation
// When: before, insert
// Order: 150

(function executeRule(current, previous /*null when async*/) {

    if (!current.parking_location || !current.start_date_time || !current.end_date_time) {
        return;
    }

    // หาจำนวนที่จอดทั้งหมด
    var location = new GlideRecord('x_1869622_parkin_0_parking_location');
    if (location.get(current.parking_location)) {
        var totalSpaces = parseInt(location.total_spaces);

        // หาจำนวนที่จองไปแล้วในช่วงเวลาที่ต้องการ
        var bookedGr = new GlideRecord('x_xxxxx_parking_reservation');
        bookedGr.addQuery('parking_location', current.parking_location);
        bookedGr.addQuery('reservation_status', 'IN', 'Pending,Confirmed');
        bookedGr.addQuery('sys_id', '!=', current.sys_id);

        // ตรวจสอบว่าช่วงเวลาทับซ้อนกัน
        bookedGr.addEncodedQuery('start_date_time<' + current.end_date_time + '^end_date_time>' + current.start_date_time);
        bookedGr.query();

        var bookedSpaces = bookedGr.getRowCount();
        var availableSpaces = totalSpaces - bookedSpaces;

        if (availableSpaces <= 0) {
            // ไม่มีที่ว่าง - ตั้งค่าสถานะเป็น Rejected
            current.reservation_status = 'Rejected';
            current.work_notes = 'การจองไม่สำเร็จ: ที่จอดรถเต็มในช่วงเวลาที่เลือก';
            gs.addInfoMessage('ขออภัย ที่จอดรถเต็มในช่วงเวลาที่เลือก กรุณาเลือกเวลาอื่นหรือสถานที่อื่น');
        } else {
            gs.addInfoMessage('พบที่ว่าง: ' + availableSpaces + ' ที่');
        }
    }

})(current, previous);
