sys_script.script:
(function executeRule(current, previous) {

    // ดึงค่า dates
    var startDateTime = current.getValue('start_date_time');
    var endDateTime = current.getValue('end_date_time');

    // ต้องมีทั้ง 2 ค่า
    if (!startDateTime || !endDateTime) {
        gs.warn('[Parking Fee] Missing start or end date');
        return;
    }

    try {
        // คำนวณ duration
        var startDate = new GlideDateTime(startDateTime);
        var endDate = new GlideDateTime(endDateTime);

        var diffMs = endDate.getNumericValue() - startDate.getNumericValue();
        var diffHours = diffMs / (1000 * 60 * 60);
        var durationHours = Math.ceil(diffHours);

        gs.info('[Parking Fee] Duration: ' + durationHours + ' hours');

        // Validate duration
        if (durationHours <= 0) {
            gs.warn('[Parking Fee] Invalid duration: ' + durationHours);
            return;
        }

        // Set duration
        current.setValue('duration_hours', durationHours);

        // คำนวณค่าจอด (Progressive rate)
        var parkingFee = 0;
        var fullDays = Math.floor(durationHours / 24);
        var remainingHours = durationHours % 24;

        // Full days: 200฿/day
        if (fullDays > 0) {
            parkingFee += fullDays * 200;
            gs.info('[Parking Fee] Full days: ' + fullDays + ' x 200 = ' + (fullDays * 200));
        }

        // Remaining hours
        if (remainingHours > 0) {
            if (remainingHours > 8) {
                // เกิน 8 ชม = flat rate 200฿
                parkingFee += 200;
                gs.info('[Parking Fee] Remaining > 8 hours: flat 200');
            } else {
                // Progressive rate
                var hoursLeft = remainingHours;

                // Tier 1: Hour 1 = 10฿
                if (hoursLeft > 0) {
                    var tier1 = Math.min(hoursLeft, 1);
                    parkingFee += tier1 * 10;
                    hoursLeft -= tier1;
                    gs.info('[Parking Fee] Tier 1: ' + tier1 + ' x 10 = ' + (tier1 * 10));
                }

                // Tier 2: Hours 2-4 = 15฿/hour
                if (hoursLeft > 0) {
                    var tier2 = Math.min(hoursLeft, 3);
                    parkingFee += tier2 * 15;
                    hoursLeft -= tier2;
                    gs.info('[Parking Fee] Tier 2: ' + tier2 + ' x 15 = ' + (tier2 * 15));
                }

                // Tier 3: Hours 5-8 = 20฿/hour
                if (hoursLeft > 0) {
                    var tier3 = Math.min(hoursLeft, 4);
                    parkingFee += tier3 * 20;
                    gs.info('[Parking Fee] Tier 3: ' + tier3 + ' x 20 = ' + (tier3 * 20));
                }
            }
        }

        // Set parking fee
        current.setValue('parking_fee', parkingFee);

        gs.info('[Parking Fee] Total fee: ' + parkingFee + ' THB');

    } catch (ex) {
        gs.error('[Parking Fee] Error: ' + ex.message);
    }

})(current, previous);
