sys_script.script:
(function executeRule(current, previous) {

    var startDateTime = current.getValue('start_date_time');
    var endDateTime = current.getValue('end_date_time');
    var now = new GlideDateTime();

    // ========================================
    // ตรวจสอบ Start Date
    // ========================================

    if (!startDateTime) {
        gs.addErrorMessage('❌ กรุณาระบุวันเริ่มต้น');
        current.setAbortAction(true);
        return;
    }

    var startDate = new GlideDateTime(startDateTime);

    // Start ไม่เป็นอดีต
    if (startDate.getNumericValue() < now.getNumericValue()) {
        gs.addErrorMessage('❌ ไม่สามารถจองเวลาย้อนหลังได้ (Start Date)');
        current.setAbortAction(true);
        return;
    }

    // ========================================
    // ตรวจสอบ End Date
    // ========================================

    if (!endDateTime) {
        gs.addErrorMessage('❌ กรุณาระบุวันสิ้นสุด');
        current.setAbortAction(true);
        return;
    }

    var endDate = new GlideDateTime(endDateTime);

    // End ต้องมากกว่า Start
    if (endDate.getNumericValue() <= startDate.getNumericValue()) {
        gs.addErrorMessage('❌ เวลาสิ้นสุดต้องมากกว่าเวลาเริ่มต้น');
        current.setAbortAction(true);
        return;
    }

    // End ไม่เป็นอดีต
    if (endDate.getNumericValue() < now.getNumericValue()) {
        gs.addErrorMessage('❌ ไม่สามารถจองเวลาย้อนหลังได้ (End Date)');
        current.setAbortAction(true);
        return;
    }

    // ========================================
    // ตรวจสอบระยะเวลาสูงสุด (optional)
    // ========================================

    var diffMs = endDate.getNumericValue() - startDate.getNumericValue();
    var diffHours = diffMs / (1000 * 60 * 60);

    // ไม่เกิน 7 วัน (168 ชม)
    if (diffHours > 168) {
        gs.addErrorMessage('❌ ไม่สามารถจองเกิน 7 วัน (168 ชั่วโมง)');
        current.setAbortAction(true);
        return;
    }

    // ต้องมีระยะเวลาอย่างน้อย 1 ชั่วโมง
    if (diffHours < 1) {
        gs.addErrorMessage('❌ ระยะเวลาต้องมากกว่า 1 ชั่วโมง');
        current.setAbortAction(true);
        return;
    }

})(current, previous);
