sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') return;

    // 1. ดึงค่าแบบ Client Script (g_form) ไม่ใช่แบบ Business Rule (current)
    var start = g_form.getValue('start_date_time');
    var end = newValue; 

    if (!start || !end) return;

    var startDate = new Date(g_form.getFormControl('start_date_time').value).getTime();
    var endDate = new Date(g_form.getFormControl('end_date_time').value).getTime();

    // 2. ด่านกักกัน: วันที่ผิดต้องโชว์แดงทันที (Validation)
    if (startDate >= endDate) {
        g_form.showFieldMsg('end_date_time', 'รันครับ! วันสิ้นสุดต้องมากกว่าวันเริ่มนะ', 'error');
        g_form.setValue('parking_fee', 0);
        g_form.setValue('duration_hours', 0);
        return;
    } else {
        g_form.hideFieldMsg('end_date_time');
    }

    // 3. คำนวณราคาแบบ Progressive (ตรรกะเดียวกับ Business Rule ของรัน)
    var diffHours = Math.ceil((endDate - startDate) / (1000 * 60 * 60));
    g_form.setValue('duration_hours', diffHours);

    var fee = 0;
    var days = Math.floor(diffHours / 24);
    var hours = diffHours % 24;

    if (days > 0) fee += (days * 200);
    if (hours > 0) {
        if (hours > 8) { fee += 200; }
        else {
            var h = hours;
            if (h > 0) { fee += 10; h--; }
            if (h > 0) { var t2 = Math.min(h, 3); fee += (t2 * 15); h -= t2; }
            if (h > 0) { fee += (h * 20); }
        }
    }
    // 4. สั่งให้ราคาเด้งโชว์บนหน้าจอ
    g_form.setValue('parking_fee', fee);
}
