sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // 1. ดึงค่าจากหน้าจอ
    var vehicle = g_form.getValue('vehicle_registration');
    var start = g_form.getValue('start_date_time');
    var end = newValue;

    // ถ้าข้อมูลยังไม่ครบ ไม่ต้องเช็ก
    if (!vehicle || !start || !end) {
        return;
    }

    // 2. เรียกใช้ Script Include (ParkingCheck) ที่เราสร้างไว้
    var ga = new GlideAjax('ParkingCheck'); 
    ga.addParam('sysparm_name', 'checkDuplicate'); // เรียกฟังก์ชัน
    ga.addParam('sysparm_vehicle', vehicle);
    ga.addParam('sysparm_start', start);
    ga.addParam('sysparm_end', end);
    ga.addParam('sysparm_id', g_form.getUniqueValue()); // ส่ง ID ตัวเองไปด้วย

    // 3. รอรับผลลัพธ์
    ga.getXMLAnswer(function(answer) {
        if (answer) {
            // ถ้ามีค่าส่งกลับมา แปลว่า "ซ้ำ"
            g_form.showFieldMsg('end_date_time', 'ไม่ได้ครับ! ทะเบียนนี้จองไว้แล้วที่ ' + answer, 'error');
            // g_form.setValue('end_date_time', ''); // (ทางเลือก) ล้างค่าทิ้งเลยถ้าต้องการ
        } else {
            // ถ้าว่างเปล่า แปลว่า "ผ่าน"
            g_form.hideFieldMsg('end_date_time');
        }
    });
}
