sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    // 1. ถ้ากำลังโหลด หรือไม่มีค่าใหม่ ให้จบการทำงาน
    if (isLoading || newValue === '') {
        return;
    }

    // ล้างข้อความแจ้งเตือนเก่าก่อนเสมอ
    g_form.hideFieldMsg('start_date_time', true);

    // 2. ดึงค่าเวลาปัจจุบันและเวลาที่เลือก
    // หมายเหตุ: ใช้ new Date(newValue) อาจมีปัญหากับบาง Format แนะนำให้ใช้ g_form.getValue ถ้ามีปัญหา
    var startDate = new Date(newValue);
    var now = new Date();

    // ========================================
    // ด่านที่ 1: ตรวจสอบ Start ห้ามเป็นอดีต
    // ========================================
    if (startDate < now) {
        g_form.showFieldMsg('start_date_time', '❌ ไม่สามารถจองเวลาย้อนหลังได้ครับ', 'error');

        // ล้างค่าทิ้งทั้งหมด
        g_form.clearValue('start_date_time');
        g_form.clearValue('end_date_time');
        g_form.clearValue('duration_hours');
        g_form.clearValue('parking_fee');

        return; // *** สำคัญ: ต้องจบการทำงานตรงนี้ ไม่งั้นจะไปโชว์ Success ต่อ
    }

    // ========================================
    // ด่านที่ 2: ตรวจสอบเทียบกับ End Date (ถ้ามี)
    // ========================================
    var endDateTime = g_form.getValue('end_date_time');

    if (endDateTime) {
        var endDate = new Date(endDateTime);

        // ถ้า End <= Start (เวลาจบ น้อยกว่าหรือเท่ากับ เวลาเริ่ม)
        if (endDate <= startDate) {
            g_form.showFieldMsg('start_date_time', '⚠️ เวลาเริ่มต้นต้องมาก่อนเวลาสิ้นสุดนะครับ', 'error'); // เปลี่ยนเป็น error สีแดงจะเห็นชัดกว่า warning

            // ล้างเวลาจบ เพื่อให้เลือกใหม่
            g_form.clearValue('end_date_time');
            g_form.clearValue('duration_hours');
            g_form.clearValue('parking_fee');

            return; // *** สำคัญ: จบการทำงานตรงนี้
        }
    }

    // ========================================
    // ถ้าผ่านทุกด่าน -> แสดง Success
    // ========================================
    g_form.showFieldMsg('start_date_time', '✓ วันที่ถูกต้อง', 'info');
}
