sys_script.script:
// Business Rule: Check Duplicate Booking
// Table: x_xxxxx_parking_reservation
// When: before, insert
// Order: 100
// Condition: [ไม่ต้องตั้ง]

(function executeRule(current, previous /*null when async*/) {

    // ตรวจสอบการจองซ้ำ
    var gr = new GlideRecord('x_1869622_parkin_0_parking_reservation');
    gr.addQuery('vehicle_registration', current.vehicle_registration);
    gr.addQuery('reservation_status', 'IN', 'Pending,Confirmed');
    gr.addQuery('sys_id', '!=', current.sys_id);

    // ตรวจสอบว่าช่วงเวลาทับซ้อนกัน
    gr.addEncodedQuery('start_date_time<' + current.end_date_time + '^end_date_time>' + current.start_date_time);
    gr.query();

    if (gr.next()) {
        gs.addErrorMessage('ทะเบียน ' + current.vehicle_registration + ' มีการจองที่จอดรถในช่วงเวลานี้แล้วที่ ' + gr.parking_location.getDisplayValue());
        current.setAbortAction(true);
    }

})(current, previous);
